
public class IfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Temp;
		Temp = 40;
		
		if (Temp > 40) { 
			System.out.println("It's ");
			System.out.println("warm ");
		}
		if (Temp <= 40) {
			System.out.println("It's ");
			System.out.println("cool ");
		}
	}

}
