
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day;
		
		day = 2;
		
		switch (day) {
		case 1:
			System.out.println("Monday");
			break ;
		case 2:
			System.out.println("Tuesday");
			
		default :
			System.out.println("Other");
			break;
			
		}
	}

}
