
public class WeatherElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int temp = 40;
		
		if (temp > 40)
		{
			System.out.println("It ");
			System.out.println("is ");
			System.out.println("warm ");
		}
		else
		{
			System.out.println("It ");
			System.out.println("is ");
			System.out.println("cold ");
		}
	}

}
